module.exports = function FormatCollectionName( collection ) {

    // Lower case
    collection = collection.toLowerCase();

    // Plurial
    if ( collection.charAt( collection.length - 1 ) != 's' )
        collection += 's';

    // English plurial exceptions
    if ( collection == 'persons' || collection == 'peoples' )
        collection = 'people';

    return collection;

};
