module.exports = function transformInsert( args ) {

    if ( Array.isArray( args[ 0 ] ) ) {
        let d = new Date();
        args[ 0 ].map( entry => {
            if ( !entry.createdAt )
                entry.createdAt = d;
        } );
    } else if ( !args[ 0 ].createdAt )
        args[ 0 ].createdAt = new Date();

    return args;

};
