  import React, { useState, useEffect } from 'react';
  import { Divider, Box, Heading } from '@chakra-ui/react';
  import { Button } from '@chakra-ui/react';
  import { Checkbox, useCheckboxGroup } from '@chakra-ui/react';

  import './profesorado.css';

  function Profesorado() {
    const [data, setData] = useState([]);
    const [isChecked, setIsChecked] = useState([false, false, false, false]);
    const [searchValue, setSearchValue] = useState('');

    const handleCheckboxChange = (index) => {
      const newChecked = [...isChecked];
      newChecked[index] = !newChecked[index];
      setIsChecked(newChecked);
    };

    useEffect(() => {
      fetch('https://jsonplaceholder.typicode.com/photos')
        .then(response => response.json())
        .then(data => setData(data))
        .catch(error => console.error(error));
    }, []);

    const filteredData = data.filter(photo =>
      photo.title.toLowerCase().includes(searchValue.toLowerCase())
    );

    return (
      <>

        <Box bg='tomato' w='100%' p={4} color='white'>
        <Heading>Filtros</Heading>
        </Box>

        <input
            type="text"
            value={searchValue}
            onChange={event => setSearchValue(event.target.value)}
            placeholder="Search by name"
        />
        <div className='scrollbox'>
          <h1>Hola Mundo</h1>
          {filteredData.map(photo => (
            <div className='info' key={photo.id}>
              <h1>{photo.title}</h1>
              <h2>{photo.thumbnailUrl}</h2>
              <h3>{photo.thumbnailUrl}</h3>
              <Divider orientation='horizontal' />
            </div>
          ))}
        </div>
        <div className='filtros'>
          <label>
            <input
              type="checkbox"
              checked={isChecked[0]}
              onChange={() => handleCheckboxChange(0)}
            />
            Primer Filtro
          </label>

          <label>
            <input
              type="checkbox"
              checked={isChecked[1]}
              onChange={() => handleCheckboxChange(1)}
              />
            Segundo Filtro
          </label>

          <label>
            <input
              type="checkbox"
              checked={isChecked[2]}
              onChange={() => handleCheckboxChange(2)}
            />
            Tercer Filtro
          </label>

          <label>
            <input
              type="checkbox"
              checked={isChecked[3]}
              onChange={() => handleCheckboxChange(3)}
            />
            Cuarto Filtro
          </label>

          <Button colorScheme='teal' size='lg'>
              Button
          </Button>
        </div>
      </>
    );
  }

  export default Profesorado;
