import React from "react";
import { useState } from "react";
import { FormControl, FormLabel, FormHelperText, FormErrorMessage, Input } from "@chakra-ui/react";

function LoginPage() {
    const [usuarioInput, setUsuarioInput] = useState('');
    const [emailInput, setEmailInput] = useState('');
  
    const handleUsuarioInputChange = (e) => setUsuarioInput(e.target.value);
    const handleEmailInputChange = (e) => setEmailInput(e.target.value);
  
    const isUsuarioError = usuarioInput === '';
    const isEmailError = emailInput === '';
  
    return (
      <FormControl>
        <FormLabel>Nombre de usuario</FormLabel>

        <Input type='usuario' value={usuarioInput} onChange={handleUsuarioInputChange} />
        {!isUsuarioError ? (
          <FormHelperText>
            Enter your username.
          </FormHelperText>
        ) : (
          <FormErrorMessage>Username is required.</FormErrorMessage>
        )}

        <FormLabel>Email</FormLabel>
        <Input type='email' value={emailInput} onChange={handleEmailInputChange} />
        {!isEmailError ? (
          <FormHelperText>
            Enter the email you'd like to receive the newsletter on.
          </FormHelperText>
        ) : (
          <FormErrorMessage>Email is required.</FormErrorMessage>
        )}

      </FormControl>
    )
}

export default LoginPage
