  import React, { useState, useEffect } from 'react';
  import { Divider, Box, Heading, Button, Stack, Text } from '@chakra-ui/react';
  import { Checkbox, useCheckboxGroup } from '@chakra-ui/react';

  import './profesorado.css';

  function Profesorado() {
    const [data, setData] = useState([]);
    const [isChecked, setIsChecked] = useState([false, false, false, false]);
    const [searchValue, setSearchValue] = useState('');

    const handleCheckboxChange = (index) => {
      const newChecked = [...isChecked];
      newChecked[index] = !newChecked[index];
      setIsChecked(newChecked);
    };

    useEffect(() => {
      fetch('https://jsonplaceholder.typicode.com/photos')
        .then(response => response.json())
        .then(data => setData(data))
        .catch(error => console.error(error));
    }, []);

    const filteredData = data.filter(photo =>
      photo.title.toLowerCase().includes(searchValue.toLowerCase())
    );

    return (
      <>

        <Box bg='gray' w='100%' p={4} color='white'>
        <Text fontSize='2xl'>Filtros</Text>
        <Stack pl={6} mt={1} spacing={1}>
          <Checkbox colorScheme='red' defaultChecked>
            Primer Filtro
          </Checkbox>
          <Checkbox colorScheme='green' defaultChecked>
            Segundo Filtro
          </Checkbox>
          <Checkbox colorScheme='green' defaultChecked>
            Tercer Filtro
          </Checkbox>
          <Checkbox colorScheme='green' defaultChecked>
            Cuatro Filtro
          </Checkbox>
        </Stack>
        </Box>

        <input
            type="text"
            value={searchValue}
            onChange={event => setSearchValue(event.target.value)}
            placeholder="Search by name"
        />
        <div className='scrollbox'>
          <h1>Hola Mundo</h1>
          {filteredData.map(photo => (
            <div className='info' key={photo.id}>
              <h1>{photo.title}</h1>
              <h2>{photo.thumbnailUrl}</h2>
              <h3>{photo.thumbnailUrl}</h3>
              <Divider orientation='horizontal' />
            </div>
          ))}
        </div>
        <div className='filtros'>
        <Box bg='gray' w='100%' p={4} color='white'>
        <Text fontSize='2xl'>Filtros</Text>
        <Stack pl={6} mt={1} spacing={1}>
          <Checkbox colorScheme='red' defaultChecked>
            Primer Filtro
          </Checkbox>
          <Checkbox colorScheme='green' defaultChecked>
            Segundo Filtro
          </Checkbox>
          <Checkbox colorScheme='green' defaultChecked>
            Tercer Filtro
          </Checkbox>
          <Checkbox colorScheme='green' defaultChecked>
            Cuatro Filtro
          </Checkbox>
        </Stack>
        </Box>

        <Button
              size='md'
              height='32px'
              width='200px'
              border='10px'
              borderColor='orange'
              bg='orange'
              padding='5%'
          >
          Aplicar
          </Button>
        </div>
      </>
    );
  }

  export default Profesorado;
